
DROP TABLE IF EXISTS `{~dbprefix~}cms_article`;

CREATE TABLE `{~dbprefix~}cms_article` (
  `art_id` int(11) NOT NULL AUTO_INCREMENT,
  `art_mid` int(11) NOT NULL DEFAULT '0' COMMENT '菜单主ID',
  `art_msid` varchar(64) NOT NULL DEFAULT '' COMMENT '菜单副ID',
  `art_lbid` varchar(64) NOT NULL DEFAULT '' COMMENT '标签ID',
  `art_attach` varchar(128) NOT NULL DEFAULT '' COMMENT '附件1',
  `art_attach2` varchar(128) NOT NULL DEFAULT '' COMMENT '附件2',
  `art_attach3` varchar(128) NOT NULL DEFAULT '' COMMENT '附件3',
  `art_title` varchar(128) NOT NULL DEFAULT '' COMMENT 'Meta标题',
  `art_keyword` varchar(128) NOT NULL DEFAULT '' COMMENT 'Meta关键字',
  `art_description` varchar(255) NOT NULL DEFAULT '' COMMENT 'Meta描述',
  `art_content` text COMMENT '文章内容',
  `art_addtime` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '创建时间',
  `art_editime` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '编辑时间',
  `art_enable` tinyint(1) NOT NULL DEFAULT '1' COMMENT '显示 0否 1是',
  `art_hastop` tinyint(1) NOT NULL DEFAULT '0' COMMENT '置顶 0否 1是',
  `lang` varchar(8) NOT NULL DEFAULT 'cn' COMMENT '语言',
  PRIMARY KEY (`art_id`),
  KEY `art_mid` (`art_mid`),
  KEY `art_msid` (`art_msid`),
  KEY `art_lbid` (`art_lbid`),
  KEY `art_title` (`art_title`),
  KEY `art_addtime` (`art_addtime`,`art_editime`),
  KEY `art_enable` (`art_enable`,`art_hastop`),
  KEY `lang` (`lang`)
) ENGINE=MyISAM AUTO_INCREMENT=1 DEFAULT CHARSET=utf8 COMMENT='文章列表';

insert  into `{~dbprefix~}cms_article`(`art_id`,`art_mid`,`art_msid`,`art_lbid`,`art_attach`,`art_attach2`,`art_attach3`,`art_title`,`art_keyword`,`art_description`,`art_content`,`art_addtime`,`art_editime`,`art_enable`,`art_hastop`,`lang`) values (1,15,'','','','','','偏弱走势料难改引导原油市场','','上周五黄金价格跳涨了3%，但仍位于关键的1180美元/盎司下方，可能压制金价跌回上周所创下的四年半低点。周一开盘后金价小幅回落，现报1170美元附近。小时图上看MA(14)开始掉头向下，RSI也自70反转向下，金价短期可能回落至1160美元寻找支撑。','上周五黄金价格跳涨了3%，但仍位于关键的1180美元/盎司下方，可能压制金价跌回上周所创下的四年半低点。周一开盘后金价小幅回落，现报1170美元附近。小时图上看MA(14)开始掉头向下，RSI也自70反转向下，金价短期可能回落至1160美元寻找支撑。','2016-10-25 21:40:31','2016-10-25 21:40:31',1,0,'cn'),(2,15,'','','','','','黄金反弹止步于1180美元','','上周五黄金价格跳涨了3%，但仍位于关键的1180美元/盎司下方，可能压制金价跌回上周所创下的四年半低点。周一开盘后金价小幅回落，现报1170美元附近。小时图上看MA(14)开始掉头向下，RSI也自70反转向下，金价短期可能回落至1160美元寻找支撑。','上周五黄金价格跳涨了3%，但仍位于关键的1180美元/盎司下方，可能压制金价跌回上周所创下的四年半低点。周一开盘后金价小幅回落，现报1170美元附近。小时图上看MA(14)开始掉头向下，RSI也自70反转向下，金价短期可能回落至1160美元寻找支撑。','2016-10-26 14:42:54','2016-10-26 14:42:54',1,0,'cn'),(3,16,'','','','','','扩大市场覆盖率变化引导原油市场','','','真游网不断为中国投资者带来新兴的投资机会，专业的研究实力以及卓越的服务品质，持续引领中国市场发展。<br />\r\n互联网金融市场，发展速度令人咋舌，交易量迅猛增长、客户数量直线上升、在金融投资理财市场中的份额与日俱增，整个市场保持着前所未有的高速前进势头。目前微交易更流行使越来越多的中国投资者加入到微交易的行列。<br />\r\n将来除了持续加盟具实力的交易中心，为用户提供投资灵活性外，更将会扩大市场占有率，进行全国线下推广并增设各地VIP室招待客户。','2017-03-25 16:48:31','2017-03-25 16:48:31',1,0,'cn'),(4,16,'','','','','','今晚美国API原油库存变化引导原油市场','','','<img src=\"/template/upload/image/20161025/20161025214114_51250.jpg\" alt=\"\"><br>\r\n众所周知，原油投资离不开两个重要数据，一是美国API原油库存数据，二是美国EIA原油库存数据。原油库存的数目影响了可以对通货膨胀和其他经济影响力造成影响的成品油的价格。而原油和成品油价格都会对美国这个该产品的重大消费国经济产生重要影响，也会对原油主要出口国加拿大的货币加元产生重要影响。<br>\r\n实至今日(10月25日)原油盘初，美油交投于50.41美元/桶附近，跌幅约为0.22%；布油交投于51.31美元/桶附近，跌幅约为0.21%。持续走低，近期以来，国际油价反弹至每桶50美元以上后，就似乎再度陷入了盘整隔绝，进一步上攻动力略显不足。<br>\r\n据有有关情报透露，美国将增加原油库存，预期原油仍会呈下跌趋势;也有分析师称，油价近期盘整只是蓄势，未来仍将奔向70美元；目前市场正是扑朔迷离，到底将如何发展，正等待<br>\r\nAPI原油库存数据的指引。<br>\r\n而最新的美国API原油库存数据将在北京时间10月26日凌晨04点30公布，石油市场走势如何，最新的美国API数据能否指引市场，让我们拭目以待。','2016-10-25 21:41:16','2016-10-25 21:41:16',1,0,'cn'),(5,17,'','','','','','人民币收跌与中间价续创逾六年新低','','','人民币兑美元即期周二(10月25日)续收跌，与中间价再刷新逾六年新低，但汇价跌幅已有所收窄。交易员称，美元指数涨势缩减，小幅缓和人民币下行压力，但由于客盘仍偏购汇，大行为控制贬值速度继续向市场提供美元流动性，短期内人民币偏弱走势料难改。<br />\r\n他们并指出，监管层再度向市场喊话，希望稳定汇率预期，但市场似乎对监管层的预期管理有逆反心理，因此是否见效还有待观察；美联储加息仅是近期美指强势的部分原因，欧元区面临的不确定性也被动助长美元多头人气，短期美指有可能考验100整数关口。<br />\r\n"大行有在出美元，而且出的点位距离中间价比前两天要小，今天才30多个点，应该还是想控制一下（贬值）速度吧。"一中资行交易员称。<br />\r\n香港财资协会公布的数据显示，10月25日美元/人民币(香港)即期汇率定盘价为6.7778。<br />\r\n亦有股份行交易员表示，由于人民币连续贬值，且监管层对汇价的波动容忍已经明显提高，多家机构也开始调整年内贬值幅度的判断，"以前预测6.8都不会到，现在估计年内会到6.85。自营也是买（美元）的多，大家都不怎么敢空（美元）了。"<br />\r\n中国人民银行研究局首席经济学家马骏周二称，在新定价机制下尽管人民币兑美元10月有所贬值，但兑一篮子货币保持稳定；倘若美联储（FED）12月加息，对人民币汇率影响预计有限。<br />\r\n中国央行副行长易纲周二在人民日报撰文表示，今年以来，受美联储加息预期、英国脱欧等事件影响，一些国际储备货币、可兑换货币的汇率波动幅度较大，但人民币汇率比较稳定。中国经济增长仍强劲，因此人民币汇率将继续在合理均衡的水平上保持基本稳定，不存在持续贬值的基础。<br />\r\n全球汇市方面，人民币周二触及2010年开始离岸交易以来的最低位，因美元仍全线保持强势，接近九个月高位，市场预期美国将在今年底前升息。<br />\r\n中国外汇交易中心今日更新的16点人民币兑美元参考汇率为6.7772；人民币兑欧元、日元和英镑15点的参考汇率为7.3716、6.4877和8.2819。<br />\r\n离岸市场上，1年期美元/人民币无本金交割远期报人民币6.9385元。<br />\r\n中国央行2015年8月11日完善人民币兑美元汇率中间价报价，并一次性贬值近2%，同年1 2月引入参考"收盘价+一篮子货币汇率"的中间价形成机制。中国外汇交易中心2016年1月起每日公布12次人民币兑美元参考价，8月底新增每日公布两次人民币兑欧元、日元和英镑参考价。','2016-10-31 17:00:19','2016-10-31 17:00:19',1,0,'cn'),(6,17,'','','','','','美国大选：总统与资本的爱恨情仇','','','<div style=\"text-align:center;\">\r\n	<iframe src=\"http://player.youku.com/embed/XMTc3NTIxOTMwNA==\" \'allowfullscreen\'=\"\" style=\"line-height: 1.5;\" width=\"510\" height=\"498\" frameborder=\"0\">\r\n		</iframe>\r\n			</div>','2017-04-06 23:24:43','2017-04-06 23:24:43',1,0,'cn');



DROP TABLE IF EXISTS `{~dbprefix~}cms_block`;

CREATE TABLE `{~dbprefix~}cms_block` (
  `block_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '模块列表',
  `block_sign` varchar(16) NOT NULL COMMENT '标识',
  `block_name` varchar(32) NOT NULL COMMENT '名称',
  `block_article` tinyint(1) NOT NULL DEFAULT '0' COMMENT '关联文章 0否 1是',
  `block_enable` tinyint(1) NOT NULL DEFAULT '0' COMMENT '显示 0否 1是',
  `block_system` tinyint(1) NOT NULL DEFAULT '0' COMMENT '系统 0否 1是',
  `block_sort` int(11) NOT NULL DEFAULT '0' COMMENT '排序 从小到大',
  PRIMARY KEY (`block_id`),
  KEY `block_sign` (`block_sign`),
  KEY `block_name` (`block_name`),
  KEY `block_article` (`block_article`,`block_enable`,`block_system`)
) ENGINE=MyISAM AUTO_INCREMENT=1 DEFAULT CHARSET=utf8 COMMENT='模块列表';

insert  into `{~dbprefix~}cms_block`(`block_id`,`block_sign`,`block_name`,`block_article`,`block_enable`,`block_system`,`block_sort`) values (1,'tag_list','标签列表',1,1,1,1),(2,'friend_link','友情链接',0,1,1,2),(3,'banner_slide','幻灯片列表',0,1,1,3),(4,'banner_img','Banner图片',0,1,1,4);



DROP TABLE IF EXISTS `{~dbprefix~}cms_comment`;

CREATE TABLE `{~dbprefix~}cms_comment` (
  `cmt_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '评论ID',
  `cmt_pid` int(11) NOT NULL DEFAULT '0' COMMENT '父级评论ID',
  `cmt_aid` int(11) NOT NULL DEFAULT '0' COMMENT '文章ID',
  `cmt_mid` int(11) NOT NULL DEFAULT '0' COMMENT '用户ID',
  `cmt_content` text COMMENT '评论内容',
  `cmt_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '评论时间',
  `cmt_reply` text COMMENT '评论回复',
  `cmt_status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '1未审核 2已审核',
  `lang` varchar(8) NOT NULL DEFAULT 'cn' COMMENT '语言',
  PRIMARY KEY (`cmt_id`),
  KEY `cmt_pid` (`cmt_pid`,`cmt_aid`,`cmt_mid`),
  KEY `cmt_time` (`cmt_time`),
  KEY `cmt_status` (`cmt_status`)
) ENGINE=MyISAM AUTO_INCREMENT=1 DEFAULT CHARSET=utf8 COMMENT='文章评论';

insert  into `{~dbprefix~}cms_comment`(`cmt_id`,`cmt_pid`,`cmt_aid`,`cmt_mid`,`cmt_content`,`cmt_time`,`cmt_reply`,`cmt_status`,`lang`) values (1,0,1,1,'复古款雅阁对于他的富有有太多一条大鱼他也当天一度引发诉讼的','2017-04-05 23:08:13','',2,'cn');



DROP TABLE IF EXISTS `{~dbprefix~}cms_label`;

CREATE TABLE `{~dbprefix~}cms_label` (
  `label_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '标签列表',
  `label_bs` varchar(16) NOT NULL DEFAULT '' COMMENT '模块标识',
  `label_url` varchar(255) NOT NULL DEFAULT '' COMMENT '链接',
  `label_name` varchar(128) NOT NULL DEFAULT '' COMMENT '名称',
  `label_intro` varchar(255) NOT NULL DEFAULT '' COMMENT '简介',
  `label_attach` varchar(128) NOT NULL DEFAULT '' COMMENT '附件1',
  `label_attach2` varchar(128) NOT NULL DEFAULT '' COMMENT '附件2',
  `label_attach3` varchar(128) NOT NULL DEFAULT '' COMMENT '附件3',
  `label_title` varchar(128) NOT NULL DEFAULT '' COMMENT 'Meta标题',
  `label_keyword` varchar(128) NOT NULL DEFAULT '' COMMENT 'Meta关键字',
  `label_description` varchar(255) NOT NULL DEFAULT '' COMMENT 'Meta描述',
  `label_enable` tinyint(1) NOT NULL DEFAULT '1' COMMENT '显示 0否 1是',
  `label_sort` tinyint(4) NOT NULL DEFAULT '100' COMMENT '由小到大排序',
  `lang` varchar(8) NOT NULL DEFAULT 'cn' COMMENT '语言',
  PRIMARY KEY (`label_id`),
  KEY `label_bs` (`label_bs`),
  KEY `label_name` (`label_name`),
  KEY `label_enable` (`label_enable`,`label_sort`),
  KEY `lang` (`lang`)
) ENGINE=MyISAM AUTO_INCREMENT=1 DEFAULT CHARSET=utf8 COMMENT='标签列表';

insert  into `{~dbprefix~}cms_label`(`label_id`,`label_bs`,`label_url`,`label_name`,`label_intro`,`label_attach`,`label_attach2`,`label_attach3`,`label_title`,`label_keyword`,`label_description`,`label_enable`,`label_sort`,`lang`) values (1,'banner_slide','/cn/account/trading_account','真遊网+微交易','','/upload/banner/1.jpg','','','','','',1,1,'cn'),(2,'banner_slide','/cn/account/trading_account','自动结算+三方托管','','/upload/banner/2.jpg','','','','','',1,2,'cn'),(3,'banner_slide','/cn/account/trading_account','即时资讯','','/upload/banner/3.jpg','','','','','',1,3,'cn'),(4,'banner_slide','/cn/account/trading_account','强势加盟','','/upload/banner/4.jpg','','','','','',1,4,'cn'),(5,'banner_slide','/cn/account/trading_account','开发系统','','/upload/banner/5.jpg','','','','','',1,5,'cn'),(6,'banner_img','/cn/account/trading_account','静态图片','','/upload/banner/5.jpg','','','','','',1,100,'cn');



DROP TABLE IF EXISTS `{~dbprefix~}cms_menu`;

CREATE TABLE `{~dbprefix~}cms_menu` (
  `menu_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '菜单列表',
  `menu_pid` int(11) NOT NULL DEFAULT '0' COMMENT '父级ID',
  `menu_out` tinyint(1) NOT NULL DEFAULT '0' COMMENT '外链 0否 1是',
  `menu_url` varchar(255) NOT NULL DEFAULT '' COMMENT '访问URL',
  `menu_tid` varchar(255) NOT NULL DEFAULT '' COMMENT '重定向URL',
  `menu_name` varchar(128) NOT NULL DEFAULT '' COMMENT '名称',
  `menu_sname` varchar(128) NOT NULL DEFAULT '' COMMENT '简称',
  `menu_intro` varchar(255) NOT NULL DEFAULT '' COMMENT '简介',
  `menu_attach` varchar(128) NOT NULL DEFAULT '' COMMENT '附件1',
  `menu_attach2` varchar(128) NOT NULL DEFAULT '' COMMENT '附件2',
  `menu_attach3` varchar(128) NOT NULL DEFAULT '' COMMENT '附件3',
  `menu_title` varchar(128) NOT NULL DEFAULT '' COMMENT 'Meta标题',
  `menu_keyword` varchar(128) NOT NULL DEFAULT '' COMMENT 'Meta关键字',
  `menu_description` varchar(255) NOT NULL DEFAULT '' COMMENT 'Meta描述',
  `menu_content` text COMMENT '菜单内容',
  `menu_enable` tinyint(1) NOT NULL DEFAULT '0' COMMENT '显示 0否 1是',
  `menu_sort` tinyint(4) NOT NULL DEFAULT '100' COMMENT '由小到大排序',
  `lang` varchar(8) NOT NULL DEFAULT 'cn' COMMENT '语言',
  PRIMARY KEY (`menu_id`),
  KEY `menu_pid` (`menu_pid`,`menu_out`,`menu_enable`,`menu_sort`),
  KEY `menu_url` (`menu_url`),
  KEY `menu_name` (`menu_name`),
  KEY `menu_sname` (`menu_sname`),
  KEY `menu_title` (`menu_title`),
