DROP TABLE IF EXISTS `{~dbprefix~}cms_message`;

CREATE TABLE `{~dbprefix~}cms_message` (
  `msg_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '留言列表',
  `msg_name` varchar(32) NOT NULL DEFAULT '' COMMENT '姓名',
  `msg_phone` varchar(32) NOT NULL DEFAULT '' COMMENT '电话',
  `msg_email` varchar(32) NOT NULL DEFAULT '' COMMENT '电邮',
  `msg_qqnum` varchar(32) NOT NULL DEFAULT '' COMMENT 'QQ号',
  `msg_area` varchar(64) NOT NULL DEFAULT '' COMMENT '地区',
  `msg_city` varchar(64) NOT NULL DEFAULT '' COMMENT '城市',
  `msg_content` varchar(255) NOT NULL DEFAULT '' COMMENT '内容',
  `msg_remark` varchar(255) NOT NULL DEFAULT '' COMMENT '备注', 
  `msg_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '时间',
  PRIMARY KEY (`msg_id`),
  KEY `msg_name` (`msg_name`,`msg_phone`,`msg_email`,`msg_qqnum`),
  KEY `msg_area` (`msg_area`,`msg_city`),
  KEY `msg_time` (`msg_time`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='留言列表';





DROP TABLE IF EXISTS `{~dbprefix~}cms_setting`;

CREATE TABLE `{~dbprefix~}cms_setting` (
  `set_id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'CMS配置列表',
  `set_sign` varchar(64) NOT NULL DEFAULT '' COMMENT '标识',
  `set_type` varchar(32) NOT NULL DEFAULT 'text' COMMENT '类型 boolean image text html',
  `set_name` varchar(32) NOT NULL DEFAULT '' COMMENT '名称',
  `set_value` text COMMENT '数据',
  `set_system` tinyint(1) NOT NULL DEFAULT '0' COMMENT '系统 0否 1是',
  PRIMARY KEY (`set_id`),
  KEY `set_sign` (`set_sign`),
  KEY `set_type` (`set_type`),
  KEY `set_system` (`set_system`)
) ENGINE=MyISAM AUTO_INCREMENT=1 DEFAULT CHARSET=utf8 COMMENT='cms设置';

